import './ExpenseItem.css';



function ExpenseItem(props) {
    const mes =  props.date.toLocaleString('es-AR', { month: 'long' })
    const anio =  props.date.toLocaleString('es-AR', { year: 'numeric' })
    const dia =  props.date.toLocaleString('es-AR', { day: 'numeric' })
    return (
        <div className="expense-item"> 
            <div>{mes}</div>
            <div>{anio}</div>
            <div>{dia}</div>
            <div className="expense-item__description">  
                <h2> {props.title}</h2> 
                <div className="expense-item__price"> ${props.amount} </div> 
            </div> 
        </div> 
    );
}

export default ExpenseItem;
